","keccak256":"0x58b21219689909c4f8339af00813760337f7e2e7f169a97fe49e2896dcfb3b9a","license":"MIT"},"contracts/Distributor.sol":{"content":"//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import '@openzeppelin/contracts/access/Ownable.sol';

import './libraries/SafeToken.sol';

abstract contract Distributor is Ownable {
    using SafeMath for uint256;
    using SafeToken for address;

    struct Recipient {
        uint256 lastShareIndex;
        uint256 credit;
    }
    // token => account => recipient
    mapping(address => mapping(address => Recipient)) public recipients;

    // account => shares
    mapping(address => uint256) public shares;

    // token => shareIndex
    mapping(address => uint256) public shareIndex;

    // token => totalShares
    uint256 public totalShares;

    event AddReward(address indexed token, uint256 amount, uint256 newShareIndex);
    event UpdateCredit(address indexed token, address indexed account, uint256 lastShareIndex, uint256 credit);
    event EditRecipient(address indexed account, uint256 shares, uint256 totalShares);
    event Claim(address indexed token, address indexed account, uint256 amount);

    // Valid reward tokens
    address[] public tokens;
    mapping(address => uint256) public tokenIndexes;

    constructor() {
        tokens.push(address(0));
    }

    function addReward(address token, uint256 amount) public returns (uint256 _shareIndex) {
        require(tokenIndexes[token] > 0, 'Distributor: Invalid token');
        require(amount > 0, 'Distributor: Invalid amount');

        if (totalShares == 0) return shareIndex[token];

        _shareIndex = amount.mul(2**160).div(totalShares).add(shareIndex[token]);
        shareIndex[token] = _shareIndex;

        token.safeTransferFrom(msg.sender, address(this), amount);
        emit AddReward(token, amount, shareIndex[token]);
    }

    function updateCredit(address token, address account) public returns (uint256 credit) {
        require(tokenIndexes[token] > 0, 'Distributor: Invalid token');

        uint256 _shareIndex = shareIndex[token];
        if (_shareIndex == 0) return 0;

        Recipient storage recipient = recipients[token][account];
        uint256 lastShareIndex = recipient.lastShareIndex;
        uint256 lastCredit = recipient.credit;
        uint256 _shares = shares[account];

        credit = lastCredit + _shareIndex.sub(lastShareIndex).mul(_shares) / 2**160;
        recipient.lastShareIndex = _shareIndex;
        recipient.credit = credit;

        emit UpdateCredit(token, account, _shareIndex, credit);
    }

    function claim(address token) external returns (uint256 amount) {
        return claimInternal(token, msg.sender);
    }

    function claimAll() external returns (uint256[] memory amounts) {
        amounts = new uint256[](tokens.length);
        for (uint256 i = 1; i < tokens.length; i++) {
            amounts[i] = claimInternal(tokens[i], msg.sender);
        }
    }

    function getClaimable(address token, address account) external view returns (uint256 amount) {
        uint256 _shareIndex = shareIndex[token];
        if (_shareIndex == 0) return 0;

        Recipient memory recipient = recipients[token][account];
        uint256 lastShareIndex = recipient.lastShareIndex;
        uint256 lastCredit = recipient.credit;
        uint256 _shares = shares[account];

        amount = lastCredit + _shareIndex.sub(lastShareIndex).mul(_shares) / 2**160;
    }

    function claimInternal(address token, address account) internal returns (uint256 amount) {
        require(tokenIndexes[token] > 0, 'Distributor: Invalid token');

        amount = updateCredit(token, account);
        if (amount > 0) {
            recipients[token][account].credit = 0;

            IERC20(token).transfer(account, amount);
            //token.safeTransfer(account, amount);

            emit Claim(token, account, amount);
        }
    }

    function _editRecipientInternal(address account, uint256 shares_) internal {
        for (uint256 i = 1; i < tokens.length; i++) {
            updateCredit(tokens[i], account);
        }

        //updateCredit(token, account);
        uint256 prevShares = shares[account];
        uint256 _totalShares = shares_ > prevShares
            ? totalShares.add(shares_ - prevShares)
            : totalShares.sub(prevShares - shares_);
        totalShares = _totalShares;
        shares[account] = shares_;
        emit EditRecipient(account, shares_, _totalShares);
    }

    /* Admin functions */
    function addToken(address token) external onlyOwner {
        require(tokenIndexes[token] == 0, 'Distributor: token already added');
        tokens.push(token);
        tokenIndexes[token] = tokens.length - 1;
    }

    function removeToken(address token) external onlyOwner {
        uint256 index = tokenIndexes[token];
        require(index > 0, 'Distributor: token not found');
        uint256 lastIndex = tokens.length - 1;
        if (index < lastIndex) {
            address lastToken = tokens[lastIndex];
            tokens[index] = lastToken;
            tokenIndexes[lastToken] = index;
        }
        tokens.pop();
        delete tokenIndexes[token];
    }
}
","keccak256":"0x724bd33e345075db625261f4ff0897090f57d6eff072fcccf7bb0b3ab73d0a14","license":"UNLICENSED"},"contracts/ReserveManager.sol":{"content":"//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';

import './interfaces/LendingInterfaces.sol';
import './interfaces/VelodromeInterfaces.sol';
import './libraries/SafeToken.sol';

import './RewardManager.sol';

contract ReserveManager is AccessControlUpgradeable {
    using SafeToken for address;

    bytes32 public constant DISTRIBUTOR_ROLE = keccak256('DISTRIBUTOR_ROLE');

    /* Tokens */
    address public constant usdc = 0x7F5c764cBc14f9669B88837ca1490cCa17c31607;
    address public constant velo = 0x9560e827aF36c94D2Ac33a39bCE1Fe78631088Db;

    /* OpenOcean */
    address public constant OORouter = 0x6352a56caadC4F1E25CD6c75970Fa768A3304e64;

    /* Velodrome */
    address public constant voter = 0x09236cfF45047DBee6B921e00704bed6D6B8Cf7e;

    /* Distribution */
    address public constant rewardManager = 0xC5ba10B609E8500c04884e1bcfc935B2c22654cd;
    address public constant sonneTimelock = 0x5b22BD2fC485afe2DEAf1Ac9e2fAd316dDE163B0;

    function initialize() public initializer {
        __AccessControl_init();

        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    /* Guarded Distribution Functions */
    function distributeReserves(
        IMarket usdcMarket,
        uint56 usdcAmount,
        IMarket[] calldata markets,
        uint256[] calldata amounts,
        bytes[] calldata swapQuoteData
    ) external onlyRole(DISTRIBUTOR_ROLE) {
        require(
            markets.length == amounts.length && markets.length == swapQuoteData.length,
            'ReserveManager: INVALID_INPUT'
        );

        reduceReserveInternal(usdcMarket, usdcAmount);

        for (uint256 i = 0; i < markets.length; i++) {
            reduceReserveInternal(markets[i], amounts[i]);
            address underlying = markets[i].underlying();
            swapToBaseInternal(underlying, amounts[i], swapQuoteData[i]);
        }

        uint256 distAmount = usdc.myBalance();

        usdc.safeApprove(rewardManager, distAmount);
        RewardManager(rewardManager).addRewards(distAmount, 0, 0);
    }

    function distributeVelo(address pair) external onlyRole(DISTRIBUTOR_ROLE) {
        claimVeloInternal(pair);

        uint256 distAmount = velo.myBalance();

        velo.safeApprove(rewardManager, distAmount);
        RewardManager(rewardManager).addRewards(0, distAmount, 0);
    }

    /* Guarded Velodrome Management Function */
    function _stakeLP(address pair, uint256 amount) public onlyRole(DEFAULT_ADMIN_ROLE) {
        if (amount == type(uint256).max) {
            amount = pair.balanceOf(msg.sender);
        }

        pair.safeTransferFrom(msg.sender, address(this), amount);

        stakeLPInternal(pair);
    }

    function _unstakeLP(address pair, address to) public onlyRole(DEFAULT_ADMIN_ROLE) {
        unstakeLPInternal(pair);

        uint256 amount = pair.myBalance();
        pair.safeTransfer(to, amount);
    }

    /* Internal Market Management Functions */
    function reduceReserveInternal(IMarket market, uint256 amount) internal {
        market.accrueInterest();

        require(market.getCash() >= amount, 'ReserveManager: NOT_ENOUGH_CASH');
        require(market.totalReserves() >= amount, 'ReserveManager: NOT_ENOUGH_RESERVE');

        ISonneTimelock(sonneTimelock)._reduceReserves(address(market), amount, address(this));
    }

    function swapToBaseInternal(address underlying, uint256 amount, bytes memory swapQuoteDatum) internal {
        underlying.safeApprove(OORouter, amount);

        (bool success, bytes memory result) = OORouter.call{value: 0}(swapQuoteDatum);
        require(success, 'ReserveManager: OO_API_SWAP_FAILED');
    }

    /* Internal Velodrome Management Functions */
    function stakeLPInternal(address pair) internal {
        address gauge = IVoter(voter).gauges(pair);

        uint256 amountPair = pair.myBalance();
        pair.safeApprove(gauge, amountPair);
        IGauge(gauge).deposit(amountPair, 0);
    }

    function unstakeLPInternal(address pair) internal {
        address gauge = IVoter(voter).gauges(pair);
        IGauge(gauge).withdrawAll();
    }

    function claimVeloInternal(address pair) internal {
        address[] memory tokens = new address[](1);
        tokens[0] = velo;

        address gauge = IVoter(voter).gauges(pair);
        IGauge(gauge).getReward(address(this), tokens);
    }
}
","keccak256":"0xba1eee963283645a97c8a71875bc3096aa16951fe1d40d7e6e85637541fe2450","license":"UNLICENSED"},"contracts/RewardManager.sol":{"content":"//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';

import './interfaces/VelodromeInterfaces.sol';
import './libraries/SafeToken.sol';

import './StakedDistributor.sol';

contract RewardManager is OwnableUpgradeable {
    using SafeToken for address;

    StakedDistributor public immutable sSonne = StakedDistributor(0xDC05d85069Dc4ABa65954008ff99f2D73FF12618);
    StakedDistributor public immutable uSonne = StakedDistributor(0x41279e29586EB20f9a4f65e031Af09fced171166);

    address public immutable sonne = 0x1DB2466d9F5e10D7090E7152B68d62703a2245F0;
    address public immutable usdc = 0x7F5c764cBc14f9669B88837ca1490cCa17c31607;
    address public immutable velo = 0x9560e827aF36c94D2Ac33a39bCE1Fe78631088Db;
    address public immutable op = 0x4200000000000000000000000000000000000042;

    IRouter public immutable router = IRouter(0x9c12939390052919aF3155f41Bf4160Fd3666A6f);

    function initialize() public initializer {
        __Ownable_init();
    }

    function addRewards(uint256 usdcAmount, uint256 veloAmount, uint256 opAmount) public {
        // calculate supplies
        uint256 sSupply = sSonne.totalSupply();
        uint256 uSupply = uSonne.totalSupply();
        uint256 totalSupply = sSupply + uSupply;

        // add velo
        if (veloAmount > 0) {
            pullTokenInternal(velo, veloAmount);
            addTokenRewardInternal(velo, uSupply, totalSupply);
        }

        // add op
        if (opAmount > 0) {
            pullTokenInternal(op, opAmount);
            addTokenRewardInternal(op, uSupply, totalSupply);
        }

        // add usdc
        if (usdcAmount > 0) {
            pullTokenInternal(usdc, usdcAmount);
            addUSDCRewardInternal(uSupply, totalSupply);
        }
    }

    function pullTokenInternal(address token, uint256 amount) internal {
        if (amount == type(uint256).max) {
            amount = token.balanceOf(msg.sender);
        }

        token.safeTransferFrom(msg.sender, address(this), amount);
    }

    function addTokenRewardInternal(address token, uint256 uSupply, uint256 totalSupply) internal {
        uint256 amount = token.balanceOf(address(this));
        if (amount == 0) return;

        uint256 uAmount = (amount * uSupply) / totalSupply;
        uint256 sAmount = amount - uAmount;

        // add to uSonne
        token.safeApprove(address(uSonne), uAmount);
        uSonne.addReward(token, uAmount);

        // add to sSonne
        token.safeApprove(address(sSonne), sAmount);
        sSonne.addReward(token, sAmount);
    }

    function addUSDCRewardInternal(uint256 uSupply, uint256 totalSupply) internal {
        uint256 amount = usdc.balanceOf(address(this));
        if (amount == 0) return;

        uint256 uUSDCAmount = (amount * uSupply) / totalSupply;
        uint256 sUSDCAmount = amount - uUSDCAmount;

        // add to uSonne
        usdc.safeApprove(address(uSonne), uUSDCAmount);
        uSonne.addReward(usdc, uUSDCAmount);

        // swap usdc to sonne
        swapUSDCtoSonneInternal(sUSDCAmount);

        // add to sMare
        uint256 sonneAmount = sonne.balanceOf(address(this));
        sonne.safeApprove(address(sSonne), sonneAmount);
        sSonne.addReward(sonne, sonneAmount);
    }

    function swapUSDCtoSonneInternal(uint256 usdcAmount) internal {
        IRouter.route[] memory path = new IRouter.route[](1);
        path[0] = IRouter.route({from: usdc, to: sonne, stable: false});

        usdc.safeApprove(address(router), usdcAmount);
        router.swapExactTokensForTokens(usdcAmount, 0, path, address(this), block.timestamp);
    }
}
","keccak256":"0xe05ca4e86f2cea1c954df9bece755a68b03432d4675cc378b170762681425f3e","license":"UNLICENSED"},"contracts/StakedDistributor.sol":{"content":"//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';

import './libraries/SafeToken.sol';
import './Distributor.sol';

contract StakedDistributor is Distributor, ERC20 {
    using SafeToken for address;
    struct Withdrawal {
        uint256 amount;
        uint256 releaseTime;
    }

    event Withdraw(address indexed user, uint256 amount);

    uint256 public withdrawalPendingTime = 7 * 1 days;
    mapping(address => Withdrawal) public withdrawal;

    address public immutable sonne;

    constructor(
        address sonne_,
        string memory name,
        string memory symbol
    ) ERC20(name, symbol) {
        sonne = sonne_;
    }

    function mint(uint256 amount) public {
        sonne.safeTransferFrom(msg.sender, address(this), amount);
        _mint(msg.sender, amount);
    }

    function burn(uint256 amount) public {
        if (amount > 0) {
            _burn(msg.sender, amount);
        }

        Withdrawal storage withdrawal_ = withdrawal[msg.sender];
        withdrawal_.amount = withdrawal_.amount + amount;
        withdrawal_.releaseTime = block.timestamp + withdrawalPendingTime;
    }

    function withdraw() public {
        Withdrawal storage withdrawal_ = withdrawal[msg.sender];
        require(block.timestamp >= withdrawal_.releaseTime, 'StakedDistributor: not released');
        uint256 amount = withdrawal_.amount;
        withdrawal_.amount = 0;
        sonne.safeTransfer(msg.sender, amount);
        emit Withdraw(msg.sender, amount);
    }

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 /* amount */
    ) internal override {
        if (from != address(0)) {
            _editRecipientInternal(from, balanceOf(from));
        }

        if (to != address(0)) {
            _editRecipientInternal(to, balanceOf(to));
        }
    }

    /* Admin functions */
    function setWithdrawalPendingTime(uint256 withdrawalPendingTime_) public onlyOwner {
        withdrawalPendingTime = withdrawalPendingTime_;
    }
}
","keccak256":"0xb2cec5fea949d05e72103f4bf1c8ea10904d4e8e517c87ea6a7a0cbc03e6852d","license":"UNLICENSED"},"contracts/interfaces/LendingInterfaces.sol":{"content":"//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

interface IMarket {
    function underlying() external view returns (address);

    function totalReserves() external view returns (uint256);

    function getCash() external view returns (uint256);

    function accrueInterest() external returns (uint256);

    function _reduceReserves(uint256 reduceAmount) external returns (uint256);

    /* For testing */
    function admin() external view returns (address);

    function reserveGuardian() external view returns (address);

    function _setReserveGuardian(address newReserveGuardian) external returns (uint256);

    function _setPendingAdmin(address newAdmin) external returns (uint256);

    function _acceptAdmin() external returns (uint256);
}

interface IComptroller {
    function getAllMarkets() external view returns (address[] memory);
}

interface ISonneTimelock {
    function _reduceReserves(address cToken, uint256 amount, address to) external;

    /*  For testing */
    function RESERVES_ROLE() external view returns (bytes32);

    function grantRole(bytes32 role, address account) external;
}
","keccak256":"0xda34dad3e34354601dcc934d4195aeb79c7509dd124c6f5cc15b3807d1394dd8","license":"UNLICENSED"},"contracts/interfaces/VelodromeInterfaces.sol":{"content":"//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

interface IRouter {
    struct route {
        address from;
        address to;
        bool stable;
    }

    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        route[] calldata routes,
        address to,
        uint deadline
    ) external;
}

interface IVoter {
    function gauges(address pair) external view returns (address gauge);
}

interface IGauge {
    function stake() external view returns (address token);

    function balanceOf(address account) external view returns (uint256 amount);

    function deposit(uint256 amount, uint256 tokenId) external;

    function depositAll(uint256 tokenId) external;

    function withdrawAll() external;

    function withdraw(uint256 amount) external;

    function getReward(address account, address[] memory tokens) external;
}
","keccak256":"0xe413a65173a278737cecca013a32993c461df3c049f790afd7eb6c55268e1eb0","license":"UNLICENSED"},"contracts/libraries/SafeToken.sol":{"content":"//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

interface ERC20Interface {
    function balanceOf(address user) external view returns (uint256);
}

library SafeToken {
    function myBalance(address token) internal view returns (uint256) {
        return ERC20Interface(token).balanceOf(address(this));
    }

    function balanceOf(address token, address user) internal view returns (uint256) {
        return ERC20Interface(token).balanceOf(user);
    }

    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), '!safeApprove');
    }

    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), '!safeTransfer');
    }

    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), '!safeTransferFrom');
    }

    function safeTransferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(success, '!safeTransferETH');
    }
}
